import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HomePage = () => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/reviews');
                setReviews(response.data);
            } catch (error) {
                console.error('Failed to fetch reviews:', error);
            }
        };

        fetchReviews();
    }, []);

    return (
        <main className='w-full flex flex-col gap-8 justify-center items-center h-auto'>
            <h1 className='text-center text-2xl font-semibold'>Welcome to Book Nook ðŸ‘‹</h1>
            <div>
                <Link to={'/add-review'} type="button" className="text-white bg-gray-800 hover:bg-gray-900 focus:outline-none focus:ring-4 focus:ring-gray-300 font-medium rounded text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:hover:bg-gray-700 dark:focus:ring-gray-700 dark:border-gray-700">
                    Add Book Review
                </Link>
                <button type="button" className="text-gray-900 bg-white border border-gray-500 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-100 font-medium rounded text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700">
                    Show Book Reviews
                </button>
            </div>
            <div className='w-full max-w-4xl mx-auto'>
                {reviews.length > 0 ? (
                    reviews.map(review => (
                        <div key={review._id} className='mb-4 p-4 border border-gray-200 rounded-lg'>
                            <h2 className='text-xl font-semibold'>{review.bookTitle}</h2>
                            <h3 className='text-lg'>{review.author}</h3>
                            <p className='text-gray-700'>{review.reviewText}</p>
                            <p className='font-medium'>Rating: {review.rating}</p>
                        </div>
                    ))
                ) : (
                    <p>No reviews found.</p>
                )}
            </div>
        </main>
    );
};

export default HomePage;
