import React, { useState } from 'react';
import axios from 'axios';
import '../../index.css';
const AddReview = () => {
    const [bookTitle, setBookTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [reviewText, setReviewText] = useState('');
    const [rating, setRating] = useState(1);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No token found');
            }
            const response = await axios.post(
                'http://localhost:4000/api/reviews',
                {
                    title: bookTitle, // Ensure the field name matches backend expectations
                    author: author,   // Ensure the field name matches backend expectations
                    reviewText: reviewText,
                    rating: rating
                },
                {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );
            console.log('Review added successfully:', response.data);
            // Clear form fields after successful submission
            setBookTitle('');
            setAuthor('');
            setReviewText('');
            setRating(1);
        } catch (error) {
            if (error.response) {
                // Server responded with a status other than 200 range
                console.error('Failed to add review:', error.response.data);
            } else if (error.request) {
                // Request was made but no response received
                console.error('Failed to add review: No response received');
            } else {
                // Something happened in setting up the request
                console.error('Failed to add review:', error.message);
            }
        }
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-lg mx-auto">
            <h1 className='text-stylish font-bold text-center my-5 text-5xl'>Add Book Review</h1>
            <div className="grid grid-cols-2 gap-4 mb-5">
                <div>
                    <label htmlFor="title" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Title</label>
                    <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded focus:ring-0 focus:!border-gray-500 block w-full p-2.5" />
                </div>
                <div>
                    <label htmlFor="author" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Author</label>
                    <input type="text" id="author" value={author} onChange={(e) => setAuthor(e.target.value)} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded focus:ring-0 focus:!border-gray-500 block w-full p-2.5" />
                </div>
            
                <div>
                    <label htmlFor="rating" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Rating</label>
                    <input
                        type="number"
                        id="rating"
                        min={1}
                        max={5}
                        step={0.1}
                        value={rating}
                        onChange={(e) => setRating(e.target.value)}
                        className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded focus:ring-0 focus:!border-gray-500 block w-full p-2.5"
                        required
                    />
                </div>
            </div>
            <div className="mb-5">
                <label htmlFor="reviewText" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Review Text</label>
                <textarea
                    id="reviewText"
                    value={reviewText}
                    onChange={(e) => setReviewText(e.target.value)}
                    className='bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded focus:ring-0 focus:border-gray-500 block w-full p-2.5'
                    required
                ></textarea>
            </div>
            <div>
                <button
                    type="submit"
                    className="text-white focus:outline-none !bg-black rounded text-sm px-5 py-2.5 me-2 font-semibold w-full"
                >
                    Add Review
                </button>
            </div>
        </form>
    );
};

export default AddReview;
