import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const ReviewContext = createContext();

export const ReviewProvider = ({ children }) => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchReviews = async () => {
      const res = await axios.get('http://localhost:4000/api/reviews');
      setReviews(res.data);
    };
    fetchReviews();
  }, []);

  const addReview = async (review) => {
    const res = await axios.post('http://localhost:4000/api/reviews', review);
    setReviews([...reviews, res.data]);
  };

  const editReview = async (id, updatedReview) => {
    const res = await axios.put(`http://localhost:4000/api/reviews/${id}`, updatedReview);
    setReviews(reviews.map(review => review._id === id ? res.data : review));
  };

  const deleteReview = async (id) => {
    await axios.delete(`http://localhost:4000/api/reviews/${id}`);
    setReviews(reviews.filter(review => review._id !== id));
  };

  return (
    <ReviewContext.Provider value={{ reviews, addReview, editReview, deleteReview }}>
      {children}
    </ReviewContext.Provider>
  );
};

export default ReviewContext;
