import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { BookReviewsContext } from '../../context/Context';
import EditReviewModal from '../EditReviewModal/EditReviewModal';

const ProfilePage = () => {
    const { loggedInUser } = useContext(BookReviewsContext);
    const [userReviews, setUserReviews] = useState([]);
    const [query, setQuery] = useState('');
    const [page, setPage] = useState(1);
    const [pages, setPages] = useState(1);
    const [editModalOpen, setEditModalOpen] = useState(false);
    const [selectedReview, setSelectedReview] = useState(null);

    const fetchUserReviews = async (searchQuery = '', pageNumber = 1) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No token found');
            }

            const response = await axios.get(`http://localhost:4000/api/users/profile/reviews`, {
                headers: {
                    Authorization: `Bearer ${token}`
                },
                params: { query: searchQuery, page: pageNumber }
            });
            setUserReviews(response.data.reviews);
            setPage(response.data.page);
            setPages(response.data.pages);
        } catch (error) {
            console.error('Failed to fetch user reviews:', error.message);
        }
    };

    useEffect(() => {
        fetchUserReviews();
    }, []);

    const handleSearch = (e) => {
        e.preventDefault();
        setPage(1);
        fetchUserReviews(query, 1);
    };

    const handlePageChange = (newPage) => {
        setPage(newPage);
        fetchUserReviews(query, newPage);
    };

    const handleEdit = (review) => {
        setSelectedReview(review);
        setEditModalOpen(true);
    };

    const handleDelete = async (userReviewsId) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No token found');
            }
            console.log('Deleting review with ID:', userReviewsId);

            const response = await axios.delete(`http://localhost:4000/api/reviews/${userReviewsId}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log('Delete response:', response); // Debugging line
            setUserReviews(userReviews.filter(review => review._id !== userReviewsId));
        } catch (error) {
            console.error('Failed to delete review:', error.message);
        }
    };

    const handleCloseModal = () => {
        setEditModalOpen(false);
        setSelectedReview(null);
    };

    return (
        <div className="max-w-screen-xl mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Profile</h1>
            <div className="mb-6 p-4 bg-white rounded shadow">
                <h2 className="text-xl font-semibold">User Information</h2>
                <p><strong>Username:</strong> {loggedInUser.username}</p>
                <p><strong>Email:</strong> {loggedInUser.email}</p>
            </div>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Search by title or author"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button type="submit">Search</button>
            </form>
            <div>
                <h2 className="text-xl font-semibold mb-4">My Reviews</h2>
                {userReviews.length > 0 ? (
                    <ul>
                        {userReviews.map((review) => (
                            <li key={review._id} className="mb-4 p-4 bg-white rounded shadow">
                                <h3 className="text-lg font-semibold">{review.title}</h3>
                                <p><strong>Author:</strong> {review.author}</p>
                                <p>{review.reviewText}</p>
                                <p><strong>Rating:</strong> {review.rating}</p>
                                <button
                                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2"
                                    onClick={() => handleDelete(review._id)}
                                >
                                    Delete Review
                                </button>
                                <button
                                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                                    onClick={() => handleEdit(review)}
                                >
                                    Edit Review
                                </button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No reviews found.</p>
                )}
                <div className="pagination">
                    {Array.from({ length: pages }, (_, index) => (
                        <button
                            key={index}
                            onClick={() => handlePageChange(index + 1)}
                            disabled={index + 1 === page}
                        >
                            {index + 1}
                        </button>
                    ))}
                </div>
                {selectedReview && (
                    <EditReviewModal
                        isOpen={editModalOpen}
                        onClose={handleCloseModal}
                        review={selectedReview}
                        refreshReviews={fetchUserReviews}
                    />
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
