// src/pages/ProfilePage/ProfilePage.jsx

import React, { useContext, useEffect, useState } from 'react';
import { BookReviewsContext } from '../../context/Context';
import axios from 'axios';

const ProfilePage = () => {
  const { loggedInUser } = useContext(BookReviewsContext);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('No token found');
        }
        
        const response = await axios.get(`http://localhost:4000/api/users/${loggedInUser._id}/reviews`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setReviews(response.data);
      } catch (error) {
        console.error('Failed to fetch user reviews:', error.message);
      }
    };

    fetchReviews();
  }, []);

  return (
    <div className="profile-page">
      <h1>{loggedInUser.username}'s Profile</h1>
      <p>Email: {loggedInUser.email}</p>
      <h2>Your Reviews</h2>
      <ul>
        {reviews.map(review => (
          <li key={review._id}>
            <h3>{review.title}</h3>
            <p>{review.author}</p>
            <p>{review.reviewText}</p>
            <p>Rating: {review.rating}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProfilePage;
