import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { BookReviewsContext } from '../../context/Context';

const HomePage = () => {
    const { loggedInUser } = useContext(BookReviewsContext);
    const [reviews, setReviews] = useState([]);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [searchQuery, setSearchQuery] = useState('');

    const fetchReviews = async () => {
        try {
            const response = await axios.get(`http://localhost:4000/api/reviews/paginate?page=${page}&limit=10`);
            setReviews(response.data.reviews);
            setTotalPages(response.data.pages);
        } catch (error) {
            console.error('Failed to fetch reviews:', error.message);
        }
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:4000/api/reviews/search?query=${searchQuery}`);
            setReviews(response.data);
        } catch (error) {
            console.error('Failed to search reviews:', error.message);
        }
    };

    useEffect(() => {
        fetchReviews();
    }, [page]);

    return (
        <div className="max-w-screen-xl mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Book Reviews</h1>
            <form onSubmit={handleSearch} className="mb-4">
                <input
                    type="text"
                    placeholder="Search by title or author"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="border p-2 rounded mr-2"
                />
                <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                    Search
                </button>
            </form>
            <div>
                {reviews.length > 0 ? (
                    <ul>
                        {reviews.map((review) => (
                            <li key={review._id} className="mb-4 p-4 bg-white rounded shadow">
                                <h3 className="text-lg font-semibold">{review.title}</h3>
                                <p><strong>Author:</strong> {review.author}</p>
                                <p>{review.reviewText}</p>
                                <p><strong>Rating:</strong> {review.rating}</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No reviews found.</p>
                )}
                <div className="flex justify-between mt-4">
                    <button
                        disabled={page <= 1}
                        onClick={() => setPage(page - 1)}
                        className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
                    >
                        Previous
                    </button>
                    <button
                        disabled={page >= totalPages}
                        onClick={() => setPage(page + 1)}
                        className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
                    >
                        Next
                    </button>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
