import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Review from '../components/Review/Review';

const Homepage = () => {
    const [reviews, setReviews] = useState([]);
    const [query, setQuery] = useState('');
    const [page, setPage] = useState(1);
    const [pages, setPages] = useState(1);

    const fetchReviews = async (searchQuery = '', pageNumber = 1) => {
        try {
            const response = await axios.get(`http://localhost:4000/api/reviews/search`, {
                params: { query: searchQuery, page: pageNumber }
            });
            setReviews(response.data.reviews);
            setPage(response.data.page);
            setPages(response.data.pages);
        } catch (error) {
            console.error('Failed to fetch reviews:', error.message);
        }
    };

    useEffect(() => {
        fetchReviews();
    }, []);

    const handleSearch = (e) => {
        e.preventDefault();
        setPage(1);
        fetchReviews(query, 1);
    };

    const handlePageChange = (newPage) => {
        setPage(newPage);
        fetchReviews(query, newPage);
    };

    return (
        <div className="homepage">
            <h1>Homepage</h1>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Search by title or author"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button type="submit">Search</button>
            </form>
            <div className="reviews">
                {reviews.map((review) => (
                    <Review key={review._id} review={review} />
                ))}
            </div>
            <div className="pagination">
                {Array.from({ length: pages }, (_, index) => (
                    <button
                        key={index}
                        onClick={() => handlePageChange(index + 1)}
                        disabled={index + 1 === page}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default Homepage;
